document.write("        <li><a href='117.html'><font size=4><font color=darkPurple><b>107</b></font> - <font color=darkGreen>﻿अब अतद्‌भाव को उदाहरण पूर्वक स्पष्ट बतलाते हैं </font></font></a></li>");
document.write("        <li><a href='118.html'><font size=4><font color=darkPurple><b>108</b></font> - <font color=darkBlue>﻿अब, सर्वथा अभाव अतद्‌भाव का लक्षण है, इसका निषेध करते हैं</font></font></a></li>");
