document.write("        <li><a href='177.html'><font size=4><font color=darkPurple><b>165</b></font> - <font color=darkBlue>﻿अब यह बतलाते हैं कि कैसे स्निग्धत्व-रूक्षत्व से पिण्डपना होता है </font></font></a></li>");
