document.write("        <li><a href='190.html'><font size=4><font color=darkPurple><b>178</b></font> - <font color=darkGreen>﻿अब, ऐसा बतलाते हैं कि द्रव्यबंध का हेतु भावबंध है </font></font></a></li>");
